var g_times = 0;
function myfun() {
   document.querySelector("DIV.ivk-button.ivk-button-popup[data-ivk*='展开更多搜索结果|更快更好的搜索，就用百度APP'],DIV.popup-lead-cancel").click();
setTimeout(function(){ document.querySelector("DIV.popup-lead-cancel").click(); }, 1);
 if(g_times >= 3) {
   window.clearInterval(timer);
 }
 g_times ++;
}
var timer = setInterval(myfun,500);
