document.querySelector('[class^="arrow"]')?.click();
document.querySelectorAll('[class^="guideLayerCancel"]').forEach(a => a.click());
//虎扑;
document.querySelector("div.open-btn")?.click();
document.querySelector("div.cancel-btn")?.click();
