document.querySelector("section#artLookAll.look-all")?.click();
//简书;
try{document.querySelector('div.baidu-close-collapse-btn')?.click();}catch(e){}document.querySelector('button.cancel')?.click();document.querySelector("button.cancel")?.click();document.querySelector('body > div.download-app-guidance > div > div > div:nth-child(3) > div.wrap-item-btn')?.click();
  if(g_times >= 5) {
    window.clearInterval(timer);
  } 
  g_times ++;
}
