/*
 * @name: 更换字体
 * @Author: Sky
 * @version: 0.3
 * @description: 更换网页内容字体
 * @include: *
 * @createTime: 2020-6-7 18:00
 * @updateTime: 2021-7-27 12:00
 */
import config from '@bext/config';
(function(){const
/*等号后的数表示选择的字体*/
 id = config.id,
/* 0:阿里巴巴普惠体(Medium)
   1:濑户字体简体
   2:霞鹜尚智黑
   3:霞鹜文楷(等线, Bold)
   4:资源圆体(Medium) */
/*－－－－以下勿改－－－－*/
 key = encodeURIComponent('更换字体:执行判断');
 if(window[key] || navigator.connection.type != 'wifi'){return;}
 try {
  window[key] = true;
  let fn;
  switch(id){
   case 0:fn = "AliPuHui";break;
   case 1:fn = "NaihuSC";break;
   case 2:fn = "xwFasmart";break;
   case 3:fn = "xwWenKai";break;
   case 4:fn = "zyYuanTi";break;
   default:fn = "xwWenKai";
  }
  const css = document.createElement('link');
  css.href = "data:text/css,@font-face{font-family:via-font;src:url('https://cdn.staticaly.com/gh/IlysvlVEizbr/via-font@0.3/"+fn+".woff2')}html,*{font-family:via-font,sans-serif!important}";
  css.rel = 'stylesheet';
  document.head.append(css);
 } catch(err){console.log('更换字体：', err);}
})();
