import { runOnce, addStyle, runMatch, runNeed } from '@bext/util';
import { bextButton } from '@bext/ui';
import config from '@bext/config';

runOnce(() => {
    if (!config.hasOwnProperty('black')) config.black = [];
    if (!config.hasOwnProperty('white')) config.white = [];
    runMatch({
        black: config.black,
        white: config.white,
        full: true
    }).then(() => {
        runNeed(
            () => document.body,
            () => {
                let style = null,
                    doGray = (enable, btn) => {
                        if (enable) {
                            style = addStyle(':root { filter : grayscale(1) !important; }');
                            btn.btnColor = 'black';
                            btn.text = '关闭';
                        } else {
                            style.remove();
                            style = null;
                            btn.btnColor = 'deepskyblue';
                            btn.text = '纪念';
                        }
                    },
                    button = bextButton({
                        text: '纪念',
                        btnColor: 'deepskyblue',
                        textColor: 'white',
                        offsetY: window.innerHeight / 10,
                        btnStyle: 'font-size: 12px; border-radius: 5px;',
                        click: btn => doGray(!style, btn),
                    });
                button.push();
                if (config.enable) doGray(true, button);
            });
    });
})
