import { loadScript, runOnce, runNeed } from '@bext/util';
import config from '@bext/config';
runOnce(() => {
    runNeed(() => document.body, () => {
        loadScript('https://cdn.bootcdn.net/ajax/libs/eruda/2.4.1/eruda.js',
            '673d8b09ae493ffa53bd066023ae1efe71f85a9f38336acb1946f602dac6b4f6').then(() => {
                eruda.init({ defaults: config });
            });
    });
});
